<template>
  <div ref="body" class="composition">
    <article class="image">
      <img ref="img" id="img"
           src="data:image/jpeg;base64,"
           alt="">
      <canvas ref="canvas" id="canvas"></canvas>
      <div ref="palette" class="palette">
      </div>

    </article>
        <div class="color">
          <div ref="viewColor" id="viewColor">

          </div>
        </div>
  </div>
</template>

<script>

export default {
  mounted() {

    let img = this.$refs.img
    let viewColor = this.$refs.viewColor

    let imgW = 500;
    let canvas = this.$refs.canvas
    let ctx = canvas.getContext("2d");
    let cw = (canvas.width = imgW);

    let ch = (canvas.height = img.height)


    ctx.drawImage(img, 0, 0, cw, ch);

    let imgData = ctx.getImageData(0, 0, cw, ch);
    let pixels = imgData.data;
    let thisRGB;
    let thisRGBRy;

    canvas.onmousemove = function (e) {
          let m = oMousePos(canvas, e);
          let i = (m.x + m.y * cw) * 4;
          let R = pixels[i];
          let G = pixels[i + 1];
          let B = pixels[i + 2];
          thisRGBRy = [R, G, B];
          thisRGB = `rgb(${thisRGBRy})`
          viewColor.style.backgroundColor = thisRGB;
        }
    function oMousePos(canvas, evt) {
      let ClientRect = canvas.getBoundingClientRect();
      return {
        x: Math.round(evt.clientX - ClientRect.left),
        y: Math.round(evt.clientY - ClientRect.top)
      };
    }

  }
}
</script>

